import React from "react";
import ReactDOM from "react-dom";
import reactCSS from "reactcss";
import { SketchPicker } from "react-color";
import "bootstrap/dist/css/bootstrap.min.css"
import './index.css'
class Canvas extends React.Component {
  constructor(props) {
    super(props);
    this.state = { color: this.props.color, painting: false };
    console.log(this.props.color);
    this.ctx = "";
    this.startdraw = this.startdraw.bind(this);
    this.enddraw = this.enddraw.bind(this);
    this.draw = this.draw.bind(this);
  }
  componentDidMount() {
    const canvus = document.getElementById("canvus");
    this.ctx = canvus.getContext("2d");
    canvus.height = window.innerHeight - window.innerHeight * 0.004;
    canvus.width = window.innerWidth - window.innerWidth * 0.004;
    window.addEventListener("resize", () => {
      canvus.height = window.innerHeight - window.innerHeight * 0.004;
      canvus.width = window.innerWidth - window.innerWidth * 0.004;
    });
  }
  componentDidUpdate() {
    let color =this.props.color
    console.log(color)
    if (this.state.color !== color) {
      this.setState({color:color})
    }
  }
  startdraw() {
    this.setState({ painting: true });
  }
  enddraw() {
    this.setState({ painting: false });
    this.ctx.beginPath();
  }
  draw(e) {
    if (this.state.painting) {
      this.ctx.lineWidth = 10;
      this.ctx.lineCap = "round";
      this.ctx.lineTo(e.clientX, e.clientY - 25);
      this.ctx.strokeStyle = `${this.state.color}`;
      this.ctx.stroke();
      this.ctx.beginPath();
      this.ctx.moveTo(e.clientX, e.clientY - 25);
    } else {
      return;
    }
  }

  render() {
    return (
      <canvas
        id="canvus"
        onMouseDown={this.startdraw}
        onMouseUp={this.enddraw}
        onMouseMove={this.draw}
      />
    );
  }
}

class Slider extends React.Component {
  constructor(props) {
    super(props)
    this.sliderref = React.createRef();
    this.handleChange = this.handleChange.bind(this)
  }
  componentDidMount() {
    this.sliderref.current.focus();
  }
  handleChange(event) {
    //this.setState({value: event.target.value});
    this.props.onSliderChange(event.target.value);
  }
  render() {
    const demontion = this.props.demontion
    return (
      <div className="d-flex flex-row" >
        <span className="badge badge-secondary input"><input type="range" min="1" max="100" value={demontion} id="myRange" onChange={this.handleChange} ref={this.sliderref}/></span>
        <span className="badge badge-secondary input"><input type="text" value={demontion} onChange={this.handleChange} /></span>

      </div>
    )
  }
}


class App extends React.Component {
  state = {
    displayColorPicker: false,
    color: "#000000",
    demontion: 10,
  };
  handleClick = () => {
    this.setState({ displayColorPicker: !this.state.displayColorPicker });
  };

  handleClose = () => {
    this.setState({ displayColorPicker: false });
  };

  handleChange = (color) => {
    this.setState({ color: color.hex });
    console.log(this.state.color);
  };

  onSliderChange =(demontion) => {
    this.setState({demontion:demontion})
  }
  render() {
    const styles = reactCSS({
      default: {
        color: {
          width: "36px",
          height: "14px",
          borderRadius: "2px",
          background: `${this.state.color}`,
        },
        swatch: {
          padding: "5px",
          background: "#fff",
          borderRadius: "1px",
          boxShadow: "0 0 0 1px rgba(0,0,0,.1)",
          display: "inline-block",
          cursor: "pointer",
        },
        popover: {
          position: "absolute",
          zIndex: "2",
        },
        cover: {
          position: "fixed",
          top: "0px",
          right: "0px",
          bottom: "0px",
          left: "0px",
        },
      },
    });

    return (
      <div>
        <div>
        <span className="badge badge-info containinput">
          <div style={styles.swatch} onClick={this.handleClick}>
          <div style={styles.color} />
          </div>{this.state.displayColorPicker ? (<div style={styles.popover}><div style={styles.cover} onClick={this.handleClose} /><SketchPicker color={this.state.color}onChange={this.handleChange}/></div>) : null}

        <Slider onSliderChange={this.onSliderChange} demontion={this.state.demontion}/>
        </span>
        </div>
        <Canvas color={this.state.color}  />
      </div>
    );
  }
}

export default App;

ReactDOM.render(<App />, document.getElementById("root"));
