import React from 'react';

class Canvas extends React.Component {
    constructor(props) {
        super(props) 
        this.state = {color:this.props.color}
    }
    componentDidMount() {
        this.updateCanvas();
    }
    componentDidUpdate() {
        if (this.state.color !== this.props.color) {
            this.setState({color:this.props.color})
        }
    }

    updateCanvas() {

        const canvus = document.getElementById('canvus')
        const ctx = canvus.getContext('2d');



        let painting = false;
        canvus.height = window.innerHeight - window.innerHeight * 0.004


        canvus.width = window.innerWidth - window.innerWidth * 0.004


        canvus.addEventListener("mousedown", startpostion);
        canvus.addEventListener("mouseup", endpostion);
        canvus.addEventListener("mousemove", draw);





        window.addEventListener("resize", () => {
            canvus.height = window.innerHeight - window.innerHeight * 0.004
            canvus.width = window.innerWidth - window.innerWidth * 0.004

        });



        function startpostion() {
            painting = true;
        }

        function endpostion() {
            painting = false;
            ctx.beginPath();
        }

        function draw(e) {
            if (!painting) return;
            ctx.lineWidth = 10;
            ctx.lineCap = "round";

            ctx.lineTo(e.clientX, e.clientY);
            ctx.strokeStyle = this.state.color
            ctx.stroke();
            ctx.beginPath();
            ctx.moveTo(e.clientX, e.clientY);
        }

    }
    render() {
        return (
            <canvas id="canvus" />
        );
    }
}
export default Canvas;
