class Canvas extends React.Component {
    componentDidMount() {
        this.updateCanvas();
    }
    updateCanvas() {
        const ctx = document.getElementbyId('canvas').getContext('2d');
        ctx.fillRect(0,0, 100, 100);
    }
    render() {
        return (
            <canvas id="canvas" width={300} height={300}/>
        );
    }
}
export default Canvas;
