function Canvas(props) {
    const [painting, setPainting] = useState(false);
    const [ctx , setCtx] = useState(null)
    console.log(props.color)
    function init() {
        const canvas = document.getElementById('canvus')
        const newctx = canvas.getContext("2d")
        setCtx(newctx)
        console.log(ctx)
    }

    function startdraw() {
        setPainting(true)
    }
    function enddraw() {

        setPainting(false)
        ctx.beginPath();
    }
    function draw(e) {
        if (painting) {
            ctx.lineWidth = 10;
            ctx.lineCap = "round";
            ctx.lineTo(e.clientX, e.clientY - 25);
            ctx.stroke();
            ctx.beginPath();
            ctx.moveTo(e.clientX, e.clientY -25);
        }
        else {
            return;
        }
    }


        return (
            <canvas onLoad={init} id="canvus" onMouseDown={startdraw} onMouseUp={enddraw} onMouseMove={draw}/>
        );

}
export default Canvas;
