import React from 'react';
import { SliderPicker  } from 'react-color';
import Canvus from './canvus/canvus'
class Colorpicker extends React.Component {
    constructor(props) {
        super(props)
        this.handleChange = this.handleChange.bind(this)
        this.state = {pickedcolor: "black"}
    }
    handleChange(color, event ) {
        this.setState({pickedcolor:color})
        console.log(this.state.pickedcolor.hex)
    }

    render() {
      return <SliderPicker pointer={SliderPicker.pointer} onChange={ this.handleChange } />;
    }
  }

export default Colorpicker;
