import React from 'react';
import './style.css'
class Canvas extends React.Component {
    state = {  }
    render() {
        const canvus = document.querySelector("#canvas");


const ctx = canvus.getContext("2d");
let painting = false;
canvus.height = window.innerHeight - window.innerHeight * 0.004


canvus.width = window.innerWidth - window.innerWidth * 0.004

function draw(e) {
    if (!painting) return;
    ctx.lineWidth = 5;
    ctx.lineCap = "round";

    ctx.lineTo(e.clientX, e.clientY);
    ctx.stroke();
    ctx.beginPath();
    ctx.moveTo(e.clientX, e.clientY);
}
canvus.addEventListener("mousedown", startpostion);
canvus.addEventListener("mouseup", endpostion);
canvus.addEventListener("mousemove", draw);





window.addEventListener("resize", () => {
    canvus.height = window.innerHeight - window.innerHeight * 0.004
    canvus.width = window.innerWidth - window.innerWidth * 0.004

});



function startpostion() {
    painting = true;
}

function endpostion() {
    painting = false;
    ctx.beginPath();
}

function draw(e) {
    if (!painting) return;
    ctx.lineWidth = 10;
    ctx.lineCap = "round";

    ctx.lineTo(e.clientX, e.clientY);
    ctx.stroke();
    ctx.beginPath();
    ctx.moveTo(e.clientX, e.clientY);
}
        return (
            <div id="canvas"></div>
        );
    }
}

export default Canvus;
