import React from "react";
import ReactDOM from "react-dom";
import { SketchPicker } from "react-color";
import "bootstrap/dist/css/bootstrap.min.css"
import './index.css'
import axios from 'axios'
class Canvas extends React.Component {
  constructor(props) {
    super(props);
    this.state = { color: this.props.color, painting: false, dimensions:this.props.dimensions };
    this.ctx = "";
    this.startdraw = this.startdraw.bind(this);
    this.enddraw = this.enddraw.bind(this);
    this.draw = this.draw.bind(this);
  }
  componentDidMount() {
    const canvas = document.getElementById("canvas");
    this.ctx = canvas.getContext("2d");
    canvas.height = window.innerHeight - window.innerHeight * 0.004;
    canvas.width = window.innerWidth - window.innerWidth * 0.004;
    window.addEventListener("resize", () => {
      canvas.height = window.innerHeight - window.innerHeight * 0.004;
      canvas.width = window.innerWidth - window.innerWidth * 0.004;
    });
  }
  componentDidUpdate() {
    let color =this.props.color
    if (this.state.color !== color) {
      this.setState({color:color})
    }
    let dimensions = this.props.dimensions 
    if (this.state.dimensions  !== dimensions){
      this.setState({dimensions:dimensions})
    }
  }
  startdraw() {
    this.setState({ painting: true });
  }
  enddraw() {
    this.setState({ painting: false });
    this.ctx.beginPath();
  }
  draw(e) {
    if (this.state.painting) {
      this.ctx.lineWidth = this.state.dimensions;
      this.ctx.lineCap = "round";
      this.ctx.lineTo(e.clientX, e.clientY );
      this.ctx.strokeStyle = this.state.color;
      this.ctx.stroke();
      this.ctx.beginPath();
      this.ctx.moveTo(e.clientX, e.clientY );
    } else {
      return;
    }
  }

  render() {
    return (
      <canvas
        id="canvas"
        onMouseDown={this.startdraw}
        onMouseUp={this.enddraw}
        onMouseMove={this.draw}
      />
    );
  }
}

class DimensionSelector extends React.Component {
  constructor(props) {
    super(props)
    this.handleChange = this.handleChange.bind(this)
  }

  handleChange(event) {
    this.props.onDimensionSelectorChange(event.target.value);
  }
  render() {
    const dimensions = this.props.dimensions
    return (
      <div className="d-flex flex-row" >
        <span className="badge badge-secondary input"><input type="range" min="1" max="100" value={dimensions} id="myRange" onChange={this.handleChange} /></span>
        <span className="badge badge-secondary input"><input type="text" value={dimensions} onChange={this.handleChange} /></span>
      </div>
    )
  }
}


class ColorSelector extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      displayColorPicker: false,
    };
    this.handleChange = this.handleChange.bind(this)
    this.handleClick = this.handleClick.bind(this)
    this.handleClose = this.handleClose.bind(this)


  }
  handleClick() {
    this.setState({ displayColorPicker: !this.state.displayColorPicker });
  };

  handleClose() {
    this.setState({ displayColorPicker: false });
  };

  handleChange(event) {
    this.props.onColorSelectorChange(event.hex)
  };

  render() {
    const color = this.props.color
    return (
      <div>
          <div className="changlecolorbtn"onClick={this.handleClick}>
          <div style={{background: color}} className="btncolor"/>
          </div>{this.state.displayColorPicker ? (<div className="colormenu"><div className="menucovor" onClick={this.handleClose} /><SketchPicker color={color} onChange={this.handleChange}/></div>) : null}
      </div>
    );
  }
}
class SaveCanvas extends React.Component {
  constructor(props) {
    super(props)
    this.handleClick = this.handleClick.bind(this)
  }
  handleClick() {
    this.props.onCanvasSave()
  }
  render() {
    return <button type="button" className="btn btn-secondary" onClick={this.handleClick}>Save</button>
  }
}
class Index extends React.Component {
  constructor(props) {
    super(props)
    this.state = { 
      dimensions: 10,
      color: "#000000",
    }
    this.onDimensionSelectorChange = this.onDimensionSelectorChange.bind(this)
    this.onColorSelectorChange = this.onColorSelectorChange.bind(this)
    this.onCanvasSave = this.onCanvasSave.bind(this)
  }
  onColorSelectorChange(color) {
    this.setState({color:color})
  }
  onDimensionSelectorChange(dimensions) {
    this.setState({dimensions:dimensions})
  }
  onCanvasSave() {
    const canvas = document.getElementById("canvas");
    const canvasdata = canvas.toDataURL();
    console.log(canvasdata)
    axios.post('http://localhost:5000/canvas')
  }
  render() {
    return (
      <div>

      <span className="badge badge-info containinput">
        <ColorSelector onColorSelectorChange={this.onColorSelectorChange} color={this.state.color}/>
        <DimensionSelector onDimensionSelectorChange={this.onDimensionSelectorChange} dimensions={this.state.dimensions}/>
        <SaveCanvas onCanvasSave={this.onCanvasSave}/>
      </span>
      <Canvas color={this.state.color} dimensions={this.state.dimensions}/>
      </div>
    );
  }
}

export default Index;

ReactDOM.render(<Index />, document.getElementById("root"));
